css:
div[class*="CalendarEvent/missing"] {
  background-color: #f05050 !important;
}

div[class*="CalendarEvent/downloaded"] {
  background-color: #27c24c !important;
}

div[class*="CalendarEvent/unaired"] {
  background-color: #5d9cec !important;
}

div[class*="CalendarEvent/unmonitored"] {
  background-color: #adadad !important;
}

div[class*="CalendarEvent/onAir"] {
  background-color: #ffa500 !important;
}

div[class*="CalendarEvent/downloading"] {
  background-color: #7a43b6 !important;
  color: #fff;
}

svg[class*="Icon-info"] {
  color: #515253;
}
