@-moz-document domain("kickassanime.rs"), domain("animo-pace-stream.io"), domain("haloani.ru"), domain("kickassanime.io") {
/*V3.12.10_200823*/
/*[[th]]*/
/*[[thh]]*/
/*[[hah]]*/
/*[[vvh]]*/
:root {
--minr:/*[[min]]*/;
--scnr:/*[[scn]]*/;
--acnr:/*[[acn]]*/;
--txnr:/*[[txn]]*/;
}
/*---*/
/*min*/
 .btn-outline-danger, #app.dark #footer, #app.dark #main-nav, .form-control, .input-group-text, #app.dark .navbar-nav .nav-item.active,  .container-fluid, ::-webkit-scrollbar-track, #notes, #app.dark .modal-header, #app.dark .modal-body, #app.dark .btn-primary[disabled], #app.dark .btn-primary:not([disabled]):hover, .btn.btn-default, .badge-dark, .alert-warning,  .dropdown-item:focus, .dropdown-item:hover, #app.dark .modal-footer, #app.dark .modal-header, .modal-content, .nav-tabs .nav-link.active, .video-list-nav .video-list-nav-tabs .nav-link:after, .modal-dialog .modal-header, #app.dark, .dark .modal-body, .dark .modal-footer, .dark .modal-header, div#app__loading {
    background-color: var(--minr)!important;
}
/*border color*/
 .btn.btn-default, #app.dark .navbar-nav .nav-item:hover, #app.dark #sidebar-anime-info a:hover, .btn-outline-danger, #app.dark table thead, .table thead th, .form-control, .input-group-text, .border, .modal-header,  .nav-tabs, #app.dark table tbody td, hr, .table-bordered, .table-bordered td, .table-bordered th, .btn-secondary, .modal-footer, .custom-control-input:checked~.custom-control-label:before, select, .jw-settings-submenu::-webkit-scrollbar-thumb, iframe, a.ka-url-wrapper.video-item-poster.rounded, #sidebar-anime-info .poster, .dropdown-menu, #app.dark .btn-outline-primary, #app.dark .btn-primary, .popover,.custom-switch .custom-control-label:after, .btn.btn-primary, .btn-danger, .btn-success, .img-thumbnail, .btn-outline-secondary {
    border: transparent!important;
      border-radius: 1px!important;        
}
/*acntxn*/
.vue-stars label, .vue-stars.notouch:not(.readonly):hover label {
    color: var(--acnr);
}
/*acntxnimp*/
#footer a:hover, a, .input-group-text, #app.dark #schedule-list a:hover, #app.dark .btn-primary, #app.dark .vue-back-to-top .rounded-circle svg, small#ser, .nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active, .video-js .vjs-control:before, .vjs-slider-horizontal .vjs-volume-level:before, .video-js .vjs-play-progress:before, .jw-button-color, .jw-settings-item-active::before, .vjs-resolution-button .vjs-resolution-button-staticlabel:before, .jw-icon-rewind:before, .jwplayer.jw-state-paused .jw-display .jw-icon-display:before, .jwplayer.jw-state-paused .jw-icon-playback:before, .jw-icon-volume:before, .jw-icon-fullscreen:before, .jwplayer.jw-state-playing .jw-display .jw-icon-display:before, .jwplayer.jw-state-playing .jw-icon-playback:before, .jw-icon-hd:before, #player .jw-button-color, .modal-dialog .modal-header button.close, .text-info {
    color: var(--acnr)!important;
}
/*acnb*/
 #app.dark .navbar-nav .nav-item.active,  .btn.btn-outline-info,  #app.dark .badge-primary, .badge-dark,  #reactions .reaction-items .reaction-item .reaction-item__button, #reactions-promotion .reaction-items .reaction-item .reaction-item__button, #reactions .reaction-items .reaction-item .reaction-item__button, .video-js .vjs-big-play-button {
    border: 1.5px solid var(--acnr)!important;
              border-radius: 1px
}
/*acnbg*/
 .mark, mark, ::-webkit-scrollbar-thumb, #app.dark .nav-tabs .nav-link:not(.active):hover:after, .badge-primary, .btn.btn-outline-success:hover, .alert-danger, .custom-control-input:checked~.custom-control-label:before, .video-js .vjs-volume-level, .video-js .vjs-play-progress, .jw-progress, .jw-knob, .jw-settings-submenu::-webkit-scrollbar-thumb, #player .jw-option.jw-active-option, #player .jw-progress, #player .jw-cue, #player .jw-knob, #player .jw-active-option, #player .jw-nextup-header, ::selection, .btn-success, .img-thumbnail {
    background-color: var(--acnr);
          }
/*acnbb*/
.video-list-nav{
    border-bottom: 3px solid var(--acnr);
}
/*scn*/
 button.mr-1.btn.btn-sm.btn-default:hover, #app.dark #sidebar-anime-info a:hover, #app.dark .navbar-nav .nav-item:hover, #app.dark .nav-tabs .nav-link:not(.active):hover, #app.dark .btn-primary, .btn.btn-outline-info, #app.dark .vue-back-to-top .rounded-circle, #app.dark table thead, .table tbody td:hover, table tbody td:hover, #app.dark .badge-primary, select, .dropdown-menu, #app.dark .card, .popover-body, .popover, .btn-secondary, .vjs-menu li.vjs-selected, .vjs-menu li.vjs-selected:focus, .vjs-menu li.vjs-selected:hover, .vjs-menu li:focus, .vjs-menu li:hover, .btn-outline, .video-js .vjs-big-play-button, body.dark .iziToast, #app.dark .btn-outline-primary, .btn.btn-primary, .dark table thead, .btn-danger, .btn-outline-secondary  {
    background: var(--scnr)!important;
}
/*txn*/
label, .vue-stars.notouch:not(.readonly) label:hover~label, .vue-stars input:checked~label, .btn-success, .btn-danger, .btn-outline-secondary {
    color: var(--txnr);
}
/*txnimp*/
.btn-outline-danger, #app.dark .nav-tabs .nav-link:not(.active):hover, mark, body.dark .iziToast .iziToast-message, body.dark .iziToast .iziToast-title
,  #app.dark .navbar-nav .nav-item.active a, #app.dark .navbar-nav .nav-item:hover a, .video-list-nav .video-list-nav-tabs .nav-link, .btn.btn-outline-info, #app.dark table thead, .table tbody td:hover, table tbody td:hover, #app.dark table td:hover>*, .vue-stars.notouch:not(.readonly) label:hover~label, .btn.btn-default, .alert-warning, .anime-info .info-header .title, #app.dark .btn-outline-primary, select, .dropdown-menu, #app.dark .dropdown-menu .dropdown-item, #app.dark .dropdown-menu .dropdown-item>*, .input-group>.custom-select:not(:last-child), .input-group>.form-control:not(:last-child), .text-muted, .form-control, .h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6, #app.dark #schedule-list a, #app.dark, #app.dark #footer .nav-link, #app.dark #main-nav .nav-link, #app.dark .video-item a, #app.dark .nav-tabs .nav-link:not(.active), .popover-body, #footer, #footer a, small, .anime-info .info-header .info-wrapper, #app.dark .ka-url-wrapper, #app.dark .ka-url-wrapper, #app.dark .ka-url-wrapper, #notes, .alert-danger, .ctr-wrap, #ser, .vjs-menu li.vjs-selected, .vjs-menu li.vjs-selected:focus, .vjs-menu li.vjs-selected:hover, #player .jw-text, #player .jw-option, #player .jw-toggle.jw-off, #player .jw-skip .jw-skip-icon, #player .jw-nextup-body, .dark .btn-primary, body.dark, body.dark #app__loading, .dark table thead, ::selection  {
    color: var(--txnr)!important;
}
/*animo vid player*/
.video-js .vjs-control-bar, .vjs-menu-button-popup .vjs-menu .vjs-menu-content, .jw-settings-menu {
    background: rgba(0, 0, 0, 0.35);
}
.jw-settings-topbar, body {
    background: transparent;
}
/*disabled page arrow color*/
#app.dark .btn-primary[disabled] {
    color: var(--minr)!important;
}
.mt-3, .my-3 {
    margin-top: 0px!important;
}
#notes, .fill-bg {
    background: rgba(0, 0, 0, 0.25);
    border-radius: 1px
                                            
}
.anime-info .info-header, img, svg {
    border-radius: 1px;
}
.bs-popover-auto[x-placement^=bottom] .arrow:after, .bs-popover-bottom .arrow:after {
    top: 1px;
    border-bottom-color: var(--scnr);
}
#main-nav .navbar-brand img {
    filter: contrast(2);
}
.fill-bg {
    opacity: 0;
}
.iziToast>.iziToast-body .iziToast-icon.ico-success, .iziToast>.iziToast-close {
    filter: invert(1);
}
.nav-tabs .nav-link.active, .nav-tabs .nav-link:focus {
    border: 1.5px solid var(--acnr);
    border-radius: 1px 1px 0 0;
    border-bottom: 0;
}
.close {
    text-shadow: none;
    opacity: 1;                               
}
div#ser, select#server-list, .control-wrapper, .control-wrapper.active {
    opacity: 0.5;
}
#player .jw-text {
    background: transparent;
}
    a.ka-url-wrapper.video-item-poster.rounded, .schedule-list-item{
  transition: transform .3s ease;
}

a.ka-url-wrapper.video-item-poster.rounded:hover {
  transform: translateY(-5px);
}
.schedule-list-item:hover {
transform: translatex(5px)
}
}

