css:
@-moz-document domain("google.com") {
#res .LC20lb {
	font-size: 16px;
	background-color: #0000001c;
	padding: 0.2em 0.7em;

	border: solid 1px #0000001f;
	box-shadow: 0 0 20px 1px #00000026;
	border-radius: 10px;
	transition: 0.3s;
}


.rc :hover {
	color: #12fff3;
	text-shadow: 0 0 10px black;
	border-radius: 10px;
	transition: 0.3s;

	background-color: #868282;
}
}
