css:
@-moz-document url-prefix("https://rst.ua"), domain("rst.ua")
{
#rst-page-body {
    background-color: #241D1D;
}
#rst-page-wrap {
    background-color: #211E1E;
}
body {
   color: #fff;
}
.rst-uix-black {
    color: #c8aa09;
}
.rst-uix-grey {
    color: #a8a5a5;
}
#rst-ua-n-overlay, #rst-ua-n-overlay-content-side1 {
    background-color: #241c1c;
}
.rst-ocb-i-d-l-i-b, .rst-ocb-i-d-l-i-s {
    color: #fff;
}
.rst-ocb-i-a a:visited .rst-ocb-i-h {
    color: #fff !important;
}
.rst-ocb-i-premium {
    background-color: #21211a;
}
a.visited:link, a:visited {
  color: #897e7f !important;
}

.rst-ocb-i-h span {
    color: aliceblue;
}
.rst-page-oldcars-item-option-block .rst-uix-price-param .rst-uix-grey {
    color: aliceblue !important;
}
}
