css:
@-moz-document url-prefix("https://vrchat.com/home/friends") {
@media (min-width: 480px) {
    div.card > div.row > div.col-lg-7 {
        flex: 0 0 58.33333%;
        max-width: 58.33333%;
    }
}

div.col-lg-7 > h6 {
    text-transform: none;
}

div.home-content > div.css-1o8x0bc > div.friend-container > div > div.mb-1 {
    margin-bottom: .25rem !important;
    display: inline-block;
    width: calc( 50% - 5px);
    margin: 2.5px;
}

.w-100 {
    border: 0px solid white;
    border-radius: .25rem;
}

.css-1o8x0bc {
    overflow-y: hidden;
}

.online > div.row > div > h6 > a > span > .active {
    background-color: #0B0;
}

.offline > div.row > div > h6 > a > span > .active,
.offline > div.row > div > h6 > a > span > .join-me,
.offline > div.row > div > h6 > a > span > .busy {
    background-color: grey;
}

.active > div.row > div > h6 > a > span > .active,
.active > div.row > div > h6 > a > span > .join-me,
.active > div.row > div > h6 > a > span > .busy {
    background-color: yellow;
}

.css-xghvhz > .active { opacity: 0.7; }

.css-xghvhz > .offline { opacity: 0.5; }
}
