css:
@-moz-document regexp("https?\\:\\/\\/d2ga\\.net\\/sc\\.html") {
/* Body background color. */
body{
    background-color: #1B1B1D;
}

/* Chat styling. */
textarea#chat_out,textarea#cout,textarea#syslog{
    resize: none;
    width: 706px;
}
input#chat_in,input#cin{
    width: 706px;
}

/* Output background/border colors. */
textarea#chat_out,textarea#cout,textarea#syslog,input#chat_in,input#cin,select#game_list,input[id^='login_']{
    background-color: #48484a;
    border: solid 1px #000000;
}

/* Output text colors */
div#login,textarea#chat_out,textarea#cout,textarea#syslog,input#chat_in,input#cin,select#game_list,input[id^='login_']{
    color: #e8e8e8;
}
}
