css:
@-moz-document domain(off.net.mk)
{
body {
  background-color: #272822;
  color:white;
}
.field-content {
  color:white;
}
.views-row .views-field-title a {
  color: white;
}
.views-row .views-field-created {
  color: red;
}
.pane-title a, h2 {
  color:white;
}
.meteo-forecast{
	color:white;
}
.meteo-desno a, div {
  color:white;
}
/* Article */
.page-title .title span {
  color: white;
}
.content {
  color:white;
}
.prezemeno-od{
	background: black;
}
.statija .voved {
  color: white !important;
}
.views-row .views-field a {
  color: white;
}
#block-views-latest-offnet {
  background-color:white;
}
#block-offnet-services-mkd-mk .views-field-number, #block-offnet-services-mkd-mk .views-field-number .dot{
	color:#da1313;
}
.block-views ul li div span a, .block-views ul li div a span{color:black}

.page-title .title span{color:#da1313}
/*Offside*/
.ofsajd-statii{
	background-position: 0 -50px;
}
.pane-ofsajd-statii .views-field-field-kategorija-offside a{
	color:black;
}
/*Bookbox*/
.field-content, .field-content a{color:white}

}
