css:
@-moz-document 
  url-prefix("https://app.clickup.com"),
  domain("app.clickup.com")
{
	/* GLOBAL */
    #manager-header {
        top: 0px !important;
    }

    .cu-container,cu-banner-popup__container, .cu-banner-popup {
        padding-top:18px !important;                             
    }
	.cu-banner-popup__container, .cu-banner-popup{
		padding-top: 0px !important;                                            
        height: 0px !important;
        background-color: inherit !important;           
    }


    .modal-open .cu-container, .modal-open .cu-header {
        filter: none !important;
    }
  
}
