css:
body {
    background-image: -webkit-gradient(linear,left top,left bottom,from(#5482ca),to(#23b3ef));
    background-image: -webkit-linear-gradient(top,#3244f9,#0c0b0b);
    -webkit-border-radius: 2px;
    background-color: #5329dc;
    border: 1px solid #1958ff;
    border-radius: 4px;
    color: #1b7af1;
    font-family: arial,sans-serif;
    font-size: 14px;
    margin: 11px 4px;
    min-width: 54px;
    padding: 0 16px;
    text-align: center;
}
