css:
@-moz-document domain("www.inmogames.com") {

/* InmoGames v1 by ColtoN */

body {
    background-color: #161819;
}
body,
td,
th,
tr {
    color: #b32a2a;
}
.sidebar-menu > li:hover > a,
.sidebar-menu > li.active > a {
    color: #222;
    background: #f3a52d;
}

.table_list td.icon,
.table_list td.info,
.table_list td.stats {
    border-right: 1px solid #f3a52d;
}
.table_list td {
    border-bottom: 1px solid #f3a52d;
}

h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
    /* background: url(../images/theme/main_block.png) no-repeat 100% -160px; */
    /* padding-right: 9px; */
    background-color: #cc3e3e;
    padding-right: 15px;
}

div.cat_bar {
    /* background: #99abbf url(../images/theme/main_block.png) no-repeat 0 -160px; */
    /* padding-left: 9px; */
    /* height: 31px; */
    /* overflow: hidden; */
    /* margin-bottom: 1px; */
    background-color: #cc3e3e;
    padding-left: 15px;
    height: 50px;
    overflow: hidden;
    margin-bottom: 1px;
    
}

div.title_barIC h4.titlebg {
    background: #161819;
    }

.user-panel > .image > img {
    width: 100%;
    max-width: 100px;
    height: auto;
}

.user-panel > .info {
    padding: 25px 5px 5px 35px;
    line-height: 1;
    position: static;
    left: 55px;
}

.main-header {
    position: relative;
    width: auto;
    height: auto;
}

.main-header .navbar {
    height: auto;
    width: auto;
}

.navbar-profile-avatar {
    position: relative;
    top: -2px;
    width: 30px;
    height: 30px;
    margin-right: .65em;
    border: 0px solid #ddd;
    border-top-right-radius: 3px;
    border-top-left-radius: 3px;
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
}

.navbar-custom-menu > a {
    color: #ffffff;
    padding: 7px 7px;
    font-size: 12px;
    text-transform: none;
    letter-spacing: 1px;
}
}
