css:
@-moz-document domain(mafiaonline.ru) {body {
    font-size: 14px;
    line-height: 1.3;
    color: #fff;
    background:  /*[[Bg_color]]*/  !important;
}

a { color: #e2e900; text-decoration: none; cursor: pointer; }
a:hover { text-decoration: none; }
span { font-size: 14px; line-height: 15px; color: #fff; }
input, textarea, select { font-size: 12px; font-family: arial, sans-serif; }
textarea { overflow: auto; }
p { line-height: 20px; font-size: 14px; }

/*Street partiy*/
table.oldschool { border-collapse: separate; border-spacing: 7px 5px}
td.players {text-align: left; vertical-align: middle; border-radius: 8px; padding: 3px;}
.text, .chat_text,.text > a, .chat_text > * {color: /*[[font_color]]*/ !important; font-size: /*[[chat_font_size]]*/px;}
.to {color: /*[[To_color]]*/; font-size: /*[[chat_font_size]]*/px;}
.to > * {color: /*[[To_color]]*/ !important; font-size: /*[[chat_font_size]]*/px;}
.chat_system {color: /*[[System_font_color]]*/ !important; font-size: /*[[chat_font_size]]*/px;}

.chat_bg {background: /*[[Bg_color]]*/;}
table.oldschool th , .ui-button, .but ,.ui-dialog, .ui-dialog-titlebar ,.ui-dialog-buttonpane, #comment{background:#2d2f2f !important; border-radius: 8px;}
#dialog-form {background:/*[[Sec_Bg_color]]*/;}
input[type="radio"] {
    background-color: /*[[radio_col]]*/;
    border: 2px solid white;
    width: /*[[radio_size]]*/px;
    height: /*[[radio_size]]*/px;
    position: relative;
    display: inline-block;
    color:#5A009D;
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
}
 input[type='radio']:hover {
    width: /*[[radio_size]]*/px;
    height: /*[[radio_size]]*/px;
    position: relative;
    background-color: #333;
    content: '';
    display: inline-block;
    visibility: visible;
    border: 2px solid white;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
    }

/*Game */
td.game_ramkal {line-height: 50px; }
.game_ramkal img {height: 50px; }
/*PHONE */
.fas.fa-sms.phone:before{content:"/*[[phone_symb]]*/";
font-size: /*[[phone_size]]*/em;}

/*Bottom buttons images https://i.ibb.co/McpS4bJ/photo-2019-07-04-20-06-00-2.jpg*/

.send_button {background: url(/*[[Env_butt]]*/) no-repeat center;}
.send_button_ph {background: url(/*[[tel_butt]]*/) no-repeat center;}
.send_smile {background: url(/*[[op_sm_butt]]*/) no-repeat center;}
.send_smile2 {background: url(/*[[cl_sm_butt]]*/) no-repeat center;}
.send_clean{background: url(/*[[clean_butt]]*/) no-repeat center;}
.send_zamok{background: url(/*[[lock_butt]]*/) no-repeat center; }
.send_zamok2{background: url(/*[[unlock_butt]]*/) no-repeat center;}
.send_info{background: url(/*[[loc_info_butt]]*/) no-repeat center;}
.send_vverh {background: url(/*[[upd_frame_butt]]*/) no-repeat center;}
.chat_global {background: url(https://i.ibb.co/m601XW9/global-Chat.png) no-repeat center;}
.chat_global2 {background: url(https://i.ibb.co/m601XW9/global-Chat.png) no-repeat center;}
.chat_lichn {background: url(https://i.ibb.co/Hnsd6rc/private-Chat.png) no-repeat center;}
.chat_lichn2{background: url(https://i.ibb.co/Hnsd6rc/private-Chat.png) no-repeat center; }
.chat_note{background: url(https://i.ibb.co/SXY6Fpj/blocknot-Chat.png) no-repeat center;}
.chat_note2{background: url(https://i.ibb.co/SXY6Fpj/blocknot-Chat.png) no-repeat center;}
#chat_note, div#smile_div {background-color: /*[[Bg_color]]*/ !important;}

#chat_note, div#smile_div {background-color: /*[[Bg_color]]*/ !important;}

/*Info tables style */
.table_info {background-color:/*[[Bg_color]]*/; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
.table_info td{background-color:/*[[Sec_Bg_color]]*/ !important; border-radius: 8px; }

.table_info tr:nth-child(2n+1), .table_info tr:nth-child(2n){background-color:/*[[Sec_Bg_color]]*/ !important;}

/* Profile style */
#profilenav {background-color:/*[[Bg_color]]*/; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
#profilenav td{background-color:/*[[Sec_Bg_color]]*/ !important; border-radius: 8px; }
#dopinfo {background:/*[[Sec_Bg_color]]*/ !important;}

/*Forum*/
.postmsg, .form-group, .dropdown-menu.open span, #pagetext, .byuser, .modlist,.usertitle, .cell-midi, .cell-maxi, .hidden-xs, .break-word, h3, .text-center, .pull-left, .table  {color: #000;}
.col-md-2.text-center span.level {color: #000;}
.collapsed{color:red}
.panel-body p, .col-sm-2 p, .col-sm-2 span{color: #000;}
.control-label, .checkbox label, .bblinks.list-inline li {color: #000;}

/*Clans*/
#families span, .block.pun, .hidden-xs span{color: #000}

/*Log*/
.panel-body span.text{color: #000}
}
