css:
@-moz-document domain("noisli.com") {
body,
.noisli-sticky-header-div,
.footer-logo-welcome,
.noisli-popup .modal-content, #login-modal, .shepherd-element.shepherd-theme-noisli .shepherd-content,
#cookies-accept a,
.alert-modal
{
    background: #11111a !important;
}

#cookies,
#login-modal form #login-modal-input-email, #login-modal form #login-modal-input-password
{
    background: #0d0d13 !important;
}

.shepherd-element.shepherd-theme-noisli.shepherd-element-attached-right.shepherd-element-attached-middle .shepherd-content:before
{
    border-left-color: #11111a;
}
.shepherd-element.shepherd-theme-noisli.shepherd-element-attached-left.shepherd-element-attached-middle .shepherd-content:before
{
    border-right-color: #11111a;
}

#login-modal form #login-modal-input-email, #login-modal form #login-modal-input-password
{
    border: none;
}

#players img
{
    filter: invert(42%) sepia(93%) saturate(1352%) hue-rotate(87deg) brightness(119%) contrast(119%);
}
}
