css:
@-moz-document domain("bilasport.net") {
.hmbd_container {
    padding-top: 0;
}

.top_main_menu {
    top: 0;
}

/* remove the website header */
/* .page_header { display: none; } */
.logo {
    display: none;
}

/* remove right sidebar chat b$ */
.right_side {
    display: none;
}


/* remove donate */
#right_side_a {
    /* display: none; */
    visibility: hidden;
}

/* hide social share buttons */
.game_tbl_info4 {
    display: none;
}

.bg_left_side {
    width: 100%;
    position: relative;
}
/* tweak video player height */
#ifrm_holder {
    height: 72vh;
    height: 105.7%;
    /* do this to remoev black border */
}

/* default dark mode on page load */
div#pgbody {
    background: rgb(40, 40, 40);
}

/* remove the bloat text below video player */
#gamesinfo {
    display: none;
}

/* nuke the footer */
.site_footer {
    display: none;
}

/* remove left and right padding */
/* .hmbd_container {
    padding-left: 10px;
    padding-right: 10px;
} */
}
