css:
@-moz-document domain(twitch.tv) {
  .video-player__container,.highwind-video-player__container--theatre-whispers{bottom:0!important}
.whispers-threads-box__container{display:none!important;}
.right-column--theatre{width: 16%!important;}
.persistent-player--theatre{width:84%!important;}
.right-column--theatre .channel-root__right-column{width:100%!important;}
.chat-line__message{
    padding-left:.5rem!important;
     text-align: justify;
     text-justify: inter-word;
}
.right-column--theatre .simplebar-content{margin-right: -26px!important;}
}
