css:
@-moz-document url-prefix("https://www.jeuxvideo.com"), url-prefix("https://www.jeuxvideo.com") {
/* Cacher */
    
    body {
        background-color: #141516;
    }
    
    .external-links, #jv-footer .highlight, .container-welcome-home, a[target="__blank"], .video-footer-container, .jv-nav-open, .jv-header-menu .jv-nav-harassment-warning {
        display: none !important;
    }
    
    .jv-header-menu .jv-nav-account-mp .jv-nav-dropdown-container-content {
        bottom: 3.6rem;
    }
    
    /* Header */
    
    .jv-header-bottom {display: none;}
    #content {margin-top: 50px;}
    
    
    .jv-header-menu .jv-header-top, .jv-header-menu .jv-nav-open {
        background-color: /*[[headColor]]*/ !important;
    }
    
    .jv-header-menu {
        box-shadow: 0 0.3125rem 0.3125rem -0.1875rem rgba(0,0,0,0.14), 0 0.5rem 0.625rem 0.0625rem rgba(0,0,0,0.098), 0 0.1875rem 0.875rem 0.125rem rgba(0,0,0,0.082);
    }
    
    /*[[headFixed]]*/

    /*[[logoOB]]*/

    
    /* Body */
    
    #content {
        background-color: #2A3842;
        background-image: none;
    }
    
    /* Sondage */
    
    .table-ending-home .bloc-no-margin {max-width: 100%;}
    .cell-left {width: 37% !important;}
    
    a[href="/forums.htm"] {z-index: 1999999987; bottom: 4014px; right: 500px;}
    
    /* Footer */
    
    #jv-footer .legal {
        background-color: #20272D;
    }

    #jv-footer .legal .mentions {
        color: white;
    }
  
  #jv-footer {
    margin-top: 0 !important;
  }
  
@media  screen and (max-width: 1440px) {
    a[href="/forums.htm"] {z-index: 1999999987; bottom: 3922px !important; right: 430px !important;}
}
  
@media  screen and (max-width: 1366px) {
    a[href="/forums.htm"] {z-index: 1999999987; bottom: 3922px !important; right: 430px !important;}
}
    
@media screen and (max-width: 1024px) {
    a[href="/forums.htm"] {z-index: 1999999987; bottom: 3921px !important; right: 240px !important}
    }
}

@-moz-document url-prefix("https://www.jeuxvideo.com/forums/"), url-prefix("https://www.jeuxvideo.com/recherche/forums") {
/* Cacher */
    
    /* Body */
    
    a[href="/forums.htm"] {
        bottom: 0 !important;
        right: 0 !important;
        z-index: 0 !important;
    }
    


    /* JVWip */
    
    #forum-main-col {
            margin-top: 2.75rem!important;
    }

    #forum-right-col {
            position: relative !important;
            visibility: hidden !important;
            height: 0 !important;
            right: 792px !important;
    }
    
    div.panel-jv-forum {
            visibility: visible !important;
            position: absolute !important;
            margin: 0 !important;
            width: 305px !important;
            height: 30px !important;
            left: 50px !important;
            top: 0px !important;
    }

    .col-right .panel:nth-of-type(1) {
            margin-top: 0px !important;
        margin-left: 407.5px !important;
            width: 305px !important;
    }
    .col-right .panel:nth-last-of-type(5), .col-right .panel:nth-child(4), .col-right .panel:nth-child(5) {
            top: 0px !important;
            z-index: 25 !important;
    }
    .col-right .panel:nth-last-of-type(2) {
            top: 0px !important;
            z-index: 10 !important;
        left: 457.5px !important;
    }
    #forum-right-col div.panel-jv-forum div.panel-body {
        background: rgb(42, 42, 42) !important; 
        visibility: hidden !important; 
        opacity: 0.0 !important;
    }
    #forum-right-col div.panel-jv-forum:hover div.panel-body {
        visibility: visible !important;
        opacity: 1 !important;
    }
    .bloc-pre-pagi-forum, .conteneur-topic-pagi, .conteneur-messages-pagi, .form-post-topic, form .titre-head-bloc, #bloc-formulaire-forum .titre-head-bloc {
            width: 1120px !important;
    }
    
    .titre-head-bloc {
        width: 1120px !important;
    }
    
    .options-crumb {
        position: relative !important; 
        right: 0px !important;
    }
    
    #forum-right-col {
        position: relative !important;
        height: 0px !important;
    }
    
    #page-topics .col-right .panel:nth-of-type(1) {
        top: 0px !important;
    }
    
  .bloc-fil-ariane-crumb-forum .fil-ariane-crumb {
      max-width: 100% !important;
    }
    
    /* Option couleur (settingC) */
 
 .panel-heading, .bloc-liste-num-page .page-active, .bloc-spoil-jv .txt-spoil, .btn-repondre-msg, .btn-poster-msg, #discussion .titre-discut, #mp-menus .nouveau-msg a, .quick-access-bar .important, .item-home-accroche .mtag, .button-primary, .button-primary:hover, .list-msg .row-head .sup-msg, .list-msg .row-head .sujet-msg, .list-msg .row-head .date-msg, .list-msg .row-head .exp-msg, .account-number-mp.has-notif::after, .gameHeaderSubNav__itemLink--active, .gameHeaderSubNav__item--active::before {
     background-color: /*[[settingC]]*/ !important;
     border-color: /*[[settingC]]*/ !important;
    }
    
  .form-post-topic .form-control {
    border-color: /*[[settingC]]*/ !important;
    }
  
 .bloc-spoil-jv .aff-spoil, .bloc-spoil-jv .masq-spoil, #bloc-title-forum, .titre-bloc, .bloc-sondage .pied-result, .bloc-chev-pix.bloc-chev-pix-link span, .titre-head-bloc .bloc-on-right.bloc-link-plus .lien-chev, .bloc-default-profil .header h2 {
     color: /*[[settingC]]*/ !important;
    }
    
    /* JVChat */
    
    .btn-jvchat {
  margin-right: 429px;
}

#jvchat-leftbar.jvchat-leftbar-reduced {
  width: 2.2rem !important;
}

.jvchat-root {
  margin-top: 0 !important;
}

.jvchat-root .conteneur-editor {
  min-width: 103.21874rem !important;
  width: 100% !important;
}

.jvchat-root #forum-main-col {
  margin-top: 0 !important;
}

.jvchat-root #forum-right-col {
  position: relative !important;
  visibility: visible !important;
  height: 0 !important;
  right: 5000px !important;
}

.jvchat-root div.panel-jv-forum {
  visibility: visible !important;
  position: absolute !important;
  margin: 0 !important;
  width: 10px !important;
  height: 100% !important;
  left: 50px !important;
  top: 0px !important;
}

.jvchat-root .bloc-pre-pagi-forum, .jvchat-root .conteneur-topic-pagi, .jvchat-root .conteneur-messages-pagi, .jvchat-root .form-post-topic, .jvchat-root form .titre-head-bloc, .jvchat-root #bloc-formulaire-forum .titre-head-bloc {
  width: 100% !important;
}
    
    /* Retirer la pub */
    
    .show-pub, #outbrain, .outbrain, #prospect-interstice, #zone-sponso, #prospect, .OUTBRAIN {
        display: none !important;
    }
    
    .liste-rubrique>li.dropdown:last-of-type {
        display: none !important;
    }
    
    .bloc-boutique-droite {
        display: none !important;
    }
    
    .jv-search-keyword {
        display: none;
    }

#jvchat-leftbar > .panel-jv-forum {
    visibility: visible !important;
    position: absolute !important;
    margin: 0 !important;
    width: 240px !important;
    top: 0px !important;
    left: 0px !important;
}

    @media  screen and (max-width: 1440px) {
        
    .btn-jvchat {
        margin-right: 342px !important;
    }

    .bloc-pre-pagi-forum, .conteneur-topic-pagi, .conteneur-messages-pagi, .form-post-topic {
            width: 940px !important;
        }

    .titre-head-bloc {width: 940px !important;}
    form .titre-head-bloc {width: 940px !important;}
    #bloc-formulaire-forum .titre-head-bloc {width: 940px !important;}
        

    #forum-right-col {
            position: relative !important;
            visibility: hidden !important;
            height: 0 !important;
            right: 727px !important;
    }
    
    div.panel-jv-forum {
            visibility: visible !important;
            position: absolute !important;
            margin: 0 !important;
            width: 235px !important;
            height: 30px !important;
            left: 100px !important;
            top: 0px !important;
    }

    .col-right .panel:nth-of-type(1) {
            margin-top: 0px !important;
        margin-left: 350px !important;
            width: 235px !important;
    }
    .col-right .panel:nth-last-of-type(2) {
            top: 0px !important;
            z-index: 10 !important;
        left: 450px !important;
    }
    
    #content {margin-top: 3.4rem !important;}
.jvchat-root .conteneur-editor {
    min-width: 74.4rem !important;
    width: 74.4rem !important;
}
}

div.bloc-rech-forum {
        background: transparent;
    }
    div.forumAnchorWrapper {
        display: none;
    }

@media screen and (max-width: 1366px) {
    .jvchat-root .conteneur-editor {
        min-width: 69.75rem !important;
        width: 69.75rem !important;
        } 
    }   
@media screen and (max-width: 1024px) {
    .jvchat-root .conteneur-editor {
        min-width: 48.3rem !important;
        width: 48.3rem !important;
        }   
    }
}

@-moz-document url-prefix("https://www.jeuxvideo.com/jeux/") {
.gameHeaderBanner {margin-top: -5.9rem !important;}
}

/*[[jvdark]]*/

@-moz-document url-prefix("https://www.jeuxvideo.com/profil/") {
.bloc-default-profil, .info-value, .bloc-description-desc.txt-enrichi-desc-profil, .bloc-chev-pix span, .fiche-abonne, .alert-warning {
    background: #313336;
    color: #C5C8C6 !important;
}


.content-img-avatar, .bloc-default-profil table.profil-display-tab tr:nth-child(odd) {
    background: #1d1f21 !important;
}

.menu-profil .list-menu-profil .actif:before {
    background-color: #E7E7E7;
}

a, .date-cell {
    color: #8F8F8F !important;
}
a:hover, a:focus, a:active, .actif {
    color: #E7E7E7 !important;
}

.bloc-default-profil:before {
    border-top-color: #1d1f21;
}

.content-img-avatar, .bloc-default-profil, .fiche-abonne, .alert-warning {
    border: none !important;
}
}
