css:
@-moz-document url-prefix("https://www.lingq.com") {

    .sentence-text .sentence-item.blue-word::after { 
                        content: "\00B0\00a0";
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-0::after { 
                        content: "\00B9\00a0";
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-1::after { 
                        content: "\00B2\00a0";
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-2::after { 
                        content: "\00B3\00a0";
    }

    .sentence-text .sentence-item.blue-word {
                                            background-color: white;
                                            color: black;
                                            border: 4px dotted black;
                                            padding: 4px;
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-0 {
                                            background-color: white;
                                            color: black;
                                            border: 2px dashed black;
                                            padding: 4px;
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-1 {
                                            background-color: white;
                                            color: black;
                                            border: 2px dashed black;
                                            padding: 4px;
    }

    .sentence-text .sentence-item.lingq-word.lingq-status-2 {
                                            background-color: white;
                                            color: black;
                                            border: 1px dotted black;
                                            padding: 4px;
    }


    .sentence-text .sentence-item.lingq-word[class*=lingq-status-].focus-card {
                                            color: black;
                                            outline: 2px dashed black;
    }

}
