css:
@-moz-document url-prefix("http://www.folklore.org") {
.logo-bar > a {
	width: 80%;
	margin: auto auto auto 10%;
}
.redraw-placeholder > table {
	width:80%;
	margin: 20px auto;
}
input[name="username"], input[name="password"] {
	width:100%;
}
.about-bar {
	padding: 0 0 0 10% !important;
}
.attribute-box {
	display: contents;
	width: 150px;
	margin: 0 0 0 0;
}
.story-footer {
	display: block;
	margin-left: 25%;
}
.story-footer > table {
	display: block;
}
center {
	background-color: light-gray;
}
.attribute-box-contents {
	border-radius: unset;
	box-shadow: unset;
	border: unset;
	margin-top: 0;
	margin-bottom: 20px;
	padding: 10px;
}
.attribute-box-title {
	background-color: #eee;
	border-bottom: 1px solid lightgray;
	padding: 10px;
	display: flow-root;
}
.project-container {
	margin: 10px 20px 20px 20px;
}
}
