css:
@-moz-document url-prefix("https://twitter.com"), url-prefix("https://mobile.twitter.com") {

		/*margin 0*/
.r-1p0dtai > div[aria-label='Image'] {
    margin: 0 !important;
overflow: auto !important;
}
div[aria-label='Image'] > .r-1p0dtai {
    margin: 0 !important;

overflow: auto !important;
}
/* 						VW / VH means % of your view width; adjust 
						"##VW" to change maximum size 
					mainly Padding adjusts the image width, but the container above it can adjust it too
*/

		/*home feed single image only ----- */
.r-1mi0q7o > div:nth-child(2) > div:nth-child(2) > div > div > div > div > a > div  {
    margin: 0 !important;
    padding-bottom: 100%!important; 
}

.r-1mi0q7o > div:nth-child(2) > div:nth-child(2) > div > div > div > div > a {
    max-height: 70vh ;
    max-width: 100%;
    height: 100%;
    width: auto;
    margin: 0 !important; 
}
.r-1777fci.r-1mi0q7o > div:nth-child(2) > div:nth-child(3) > div > div > div > div > a > div > div.r-1adg3ll.r-13qz1uu
{
    margin: 0 !important;
    padding-bottom: 100%!important; }
.r-1777fci.r-1mi0q7o > div:nth-child(2) > div:nth-child(3) > div > div > div > div > a > div > div.r-1adg3ll.r-13qz1uu 
{
    max-height: 70vh ;
    max-width: 100%;
    height: 100%;
    width: auto;
    margin: 0 !important; 
}
		/*[[collage photo posts]]

*/

.r-1udh08x > .r-9x6qib > div.css-1dbjc4n:not(.r-1pi2tsx):not(.r-13qz1uu):not(.r-1udh08x):not(.r-qklmqi) > .r-1adg3ll > div:first-child {
    margin: 0 !important;
    padding-bottom: 0 !important; 
} 

div:not(.r-1g94qm0) > div > div.r-1udh08x:not(.r-14lw9ot) > div.css-1dbjc4n:not(.r-1pi2tsx):not(.r-13qz1uu):not(.r-1adg3ll):not(.r-1udh08x):not(.r-qklmqi) > .r-1adg3ll:not(.r-13qz1uu) {
    height: 70vh;
    width: auto;
    max-height: 100%;
    max-width: 100vw;
    padding-bottom: 0!important; 
    margin: 0 !important; 
}



