css:
body{
	background-image: none;
}
nav,div,li,ol,header,footer,section,main,aside,article,body,html {
    background: black !important;
    color: #357602 !important;
}

h1,h2,h3,h4,h5,h6,p,label,strong,em,cite,q,i,b,u,span {
    color: #357602 !important;
}

a{
    color: yellow !important;
}

button,input[type=button],input[type=reset],input[type=submit] {
    background: black !important;
    color: yellow !important;
    border: none !important;
}

input[type=text],input[type=password],input[type=url],input[type=search],
input[type=email],input[type=tel],input[type=date],input[type=month],
input[type=week],input[type=datetime],input[type=datetime-local],textarea,
input[type=number] {
    background: black !important;
    border: 1px solid #357602 !important;
    color: #357602 !important;
}
