css:
@-moz-document domain("creditkarma.ca"), domain("creditkarma.com") {
body,
.ck-background-light,
div.content-area,
header.ck-nav,
li.menu-item > a,
div.nutshell,
span.author {
    color: #D9D9D9 !important;
    background-color: #616161;
}

li.menu-item {
    background-color: transparent !important;
}

.ck-card,
.expandable-row-body,
.ca-nav-root,
.bg-white {
    background-color: #424242 !important;
}

.score-dial {
    -webkit-filter: invert(85%);
    filter: invert(85%);
}

.ck-black-80,
.ck-black-90,
h1,h2,h3,h4,h5,h6 {
    color: #e9e8e8;
}

.line-graph {
    filter: invert(25%);
}

.organism-offer__bullets-gradient {
    display: none;
}
}
