css:
@-moz-document domain(volafile.org) {

html {
    --main-bg-color: #303030;
    --snd-bg-color: #101010;
    --input-color: #131313;
    --input-disabled-color: #8e8e8e;
    --snd-input-color: #333333;
    --box-color: #282828;
    --slider-color: #707070;
    --hi-color: #282828;
    --hi-hover-color: #505050;
    --file-list-color: #242424;
    --file-color: #3a3a3a;
    --file-hover-color: #505050;
    --header-hi-color: #404040;
    --tag-color: rgba(255, 121, 121, .2);
    --tag-color-nick: rgba(157, 255, 154, .2);
    --button-color: #dcdcdc;
    --button-hi-color: #ececec;
    --button-light-color: #666666;
    --button-light-hi-color: #777777;
    --channel-color: #bd7fd7;
    --context-bg-color: #444444;
}

.chat_message .username {
    color: white;
    float: none;
}

.chat_message.user .username {
    color: yellowgreen;
}

.chat_message.donator .username {
    color: lightskyblue;
}

.chat_message.staff .username {
    color: lightsalmon;
}

.chat_message.staff:not(.profile) {
    opacity: 1;
    
}

.chat_message.staff:not(.user) .username {
    color: bisque;
}

.chat_message.admin .username {
    color: orchid;
}

.tag_key_user {
color: inherit !important;
}

.filelist_resize_bar.resizing {
        background-color: #505050
}

.file_queued {
    background-color: #404040 !important;
}

.file_uploading:before {
    background-image: repeating-linear-gradient(-45deg, #404040, #404040 25%, #404040 25%, #404040 50%);
}

.file_uploading:after {
    background-image: repeating-linear-gradient(45deg, #333333, #333333 25%, #404040 25%, #404040 50%);
}

.room_svg .room_chat,.room_svg .room_files,.room_svg .room_title {
    fill: #202020;
}

.room_svg .room_area {
    fill: #404040;
}

.upload_svg .file {
    fill: #404040;
}
.upload_svg .backgroundCloud {
    fill: #202020;
}

.url_bar_icon {
    background-color: #404040;
}
}
