css:
@-moz-document domain("bmfiddle.com") {
.item-editor {
    direction: rtl;
}
.item-txt p:last-child {
    direction: rtl;
    text-align: right;
}
.block-info, .toggle-toolbar{
    direction: rtl;
    text-align: left;
}
dt.ng-binding, dd.ng-binding {
    direction: rtl;
}
h1.global-rtl, h2.global-rtl, .help h2{
    direction: rtl;
}
ul{
    direction: rtl;
}
input.ng-pristine, textarea.ng-pristine{
    direction: rtl;
}
.help-container {
    float: right;
    margin-right: 40px;
}
.canvas .icons, .grid-layout .icons{
    left: 2px;
    right: inherit !important;
}
}
