css:
@-moz-document domain("nintenderos.com") {
body{
    color: lightgray;
}
.c-header-nav__dropdown-title{
    color: black !important;
}

span.disqus-comment-count,span.item-text span{
    color: white !important;
}

a,p.tipo, h2.titulo{
    color: white !important;
}

h1,h2,h3,h4,p,span,li{
    color: lightgray !important;
}

a.carousel-box,.c-post-following__content,.ajax-tab{
    background-color: #e60012 !important;
}

.item-text{
    text-shadow: 2px 2px 3px #000000;
}

.commentNavigation span{
    background-color: #8c8c8c !important;
}
.commentNavigation a{
    background-color: black !important;
}

.c-featured-home__nav__item{
    background-color: #333 !important;
}
.c-featured-home__nav__item svg{
    filter:invert(100%);
}

blockquote{ 
    background-color: #555 !important;
}

.top-carousel-container *,
span,
h1,h2,h3,h4,p,div.is-active,div.c-header-search__container,.c-post-following__link,.c-header-nav__dropdown-circle,
.fancybox-overlay,
.bbp-forum-icon,.bbp-forum-icon img
{ 
    background-color: transparent !important;
}

body,body div,header,footer,form{ 
    background-color: #333 !important;
}
/* Pestañas */
.comun-tabs .custom-tab{ 
    background-color: #222;
}

/* =============Buscadores y formularios==========*/
input,select,option,textarea{
    color: white !important;
    background-color: #444 !important;
    border: 1px solid gray;
}

/* Solución provisional para los comentarios de Disqus se vean mejor.*/
#disqus_thread{
    background-color: #111 !important;
}

/*1.3 Eliminar aviso de cookies*/
.as-oil-content-overlay{
    display:none;
}
}

@-moz-document url-prefix("https://www.nintenderos.com/foro-nintendo") {
.category{
    background-color: #222 !important;
}

.bbp-forums ul{
    background-color: #333 !important;
}
.bbp-forum-icon img{
    border:0;
    filter:invert(100%);
}

.bbp-topics{
    background-color: #333 !important;
    
}

.bbp-topics ul:not(.bbp-body){
    border:1px solid gray;
    background-color: #333 !important;
    
}
}
