css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.smbc-comics.com") {
  
#comicright,
#buythis,
#permalink,
#sharebar,
#blogheader,
#footer,
#extracomic,
#header,
#navtop,
.cc-navaux{
    display: none !important;
}
.cc-nav{
padding-left:100px;
}
#comicleft {
    width: 100%;
    text-align: center !important;
}
#cc-comicbody{
    width: 100%;
    background-color: #eeeeee !important;
    text-align: center !important;
}
#mainwrap{
    background-color: #eeeeee !important;
    border-top: none;
    border-bottom: none;
}
body{
    background-color: #eeeeee !important;
}
#comicleft > a{
    display: none !important;
}

  #rightside, #twitter-widget-0, #towerarea, #pixiestrip, #sharebuttons, #leaderboard, #blogarea, #blogright 
     { width: 0px !important; height: 0px !important; display: none !important; }
  
  #extracomic {position: relative; left: 610px; top: -10px;} 
  #aftercomic {
      display: inline !important;
      position: absolute;      
      top: 100px;
}

}
