css:
@-moz-document domain("nakedcapitalism.com") {
body,
html {
    background: #333!important;
    color: rgba(244, 244, 244, .8)!important;
}
.cat-item a,
.xoxo a,
.comment-respond > *,
.tip-text {
    color: rgba(244, 222, 222, .7)!important;
}
.entry-title,
.archive-title,
.widget-title,
.monthly-archive-date,
a[rel*="author"] {
    color: #dd8c56 !important;
}
.author > a,
.comment-author,
.commentlist header cite a,
.front-recent-widget > h2 {
    color: #dda656 !important;
}
button,
input,
optgroup,
select,
textarea {
    color: #2d2d2d;
    font: inherit;
    background-color: #e6d9b4
}
}
