css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sports.ru")
{

  .comments-comment_bad .comments-comment__message
  {
    color: #152be2;
  }
  .voting__result_minus .voting__info-count
  {
    color: #CB0077;
  }

	@keyframes delayedShow
  	{
    	to {
    	  visibility: visible;
  		}
	}
  .base-widget.is-active
  {
    visibility: hidden;
    animation: 0s linear 0.35s forwards delayedShow;
  }

}
