css:
@-moz-document domain("www.u17.com") {
:root {
  --head-height: 34px;
  --main-height: 98%;
}

.u17-advert,  .toso, #btn_box, #readbottom, div[id*=adv], 	[name=audio_speaker]{
    display: none  !important;
}

#current_read_image, #image_trigger{
    margin-left: 100px;
    height: var(--main-height) !important;
    width: auto  !important;
}

.comic_read_img{
    position: fixed !important;
    left: 0px;
}
.tc{
    padding-left: 0px;
}
.image_trigger, .r_btnbox, .loading, .image_cache{
    margin-top: var(--head-height) !important;
    height: var(--main-height) !important;
    width: auto  !important;
    
}
.author>a{
    border: 1px solid #fff;
}

#readtop{
    width: 742px;
    position: fixed;
    margin-left: 50px;
    padding: 4px 0 0 0;
}
.btn_add, .r_btnbox, .left, .logo{
    display: none  !important;
}

#cr_top, .cr_tb{
    width: 40px  !important;
}

.n{
    height: auto  !important;
}
}
