css:
@-moz-document url-prefix("https://www.tumblr.com/dashboard/blog/") {
#posts.posts > .post_container {
    max-width: 30;
}

#posts.posts > li.notification.last_notification {
    display: none;
}
.post_avatar {
    display: none;
}
.post_avatar_wrapper.safemode {
    display: none;
}
#posts.posts > li.post_container {
    padding-top: 6px;
}
.notification {
    display: none;
}
.post:not(.post_micro) > .post_wrapper {
    max-width: 30;
    text-align: center;
}

.avatar-shape--circle.messaging-conversation--minimized .avatar {
    display: none;
}

.peepr-drawer-container {
    overflow: hidden;
    width: 100%;
}
}
