css:
@-moz-document url-prefix("https://www.horsereality.com/horses/"), url-prefix("https://www.horsereality.nl/horses/") {
/** comp scores **/ 

#tab_achievements2 > div:nth-child(2) > div:nth-child(2) > div:nth-child(n) > div.col_90, 
#tab_achievements2 > div:nth-child(2) > div:nth-child(2) > div:nth-child(n) > div:nth-child(2), 
#tab_achievements2 > div:nth-child(2) > div:nth-child(2) > div:nth-child(n) > div:nth-child(4), 
#tab_achievements2 > div:nth-child(2) > div:nth-child(2) > div:nth-child(n) > div:nth-child(5) {
    visibility: hidden; 
}

/** confo scores **/
#tab_achievements2 > div:nth-child(2) > div:nth-child(1) > div:nth-child(n) > div:nth-child(1), 
#tab_achievements2 > div:nth-child(2) > div:nth-child(1) > div:nth-child(n) > div.col_150,
#tab_achievements2 > div:nth-child(2) > div:nth-child(1) > div:nth-child(n) > div:nth-child(4) {
    visibility: hidden; 
}

#tab_achievements2 > div:nth-child(2) > div:nth-child(1) > div.top > div {
    visibility: visible !important; 
}
}
