css:
@-moz-document url-prefix("http://arvo.network/~chat") {
@media all and (prefers-color-scheme: dark) {
    .bg-white {
        background-color: black;
    }
    * {
        background-color: black;
        color: white;
        border-color: gray;
    }
    .b--black-30 {
        border-color: gray;
        border-right: 0;
    }
    input {
        background-color: black;
    }
    a {
        color: white !important;
    }
    div.bg-light-gray, div.bg-light-gray *:not(.bg-nice-green) {
        background-color: #444 !important;
    }
    .cf.absolute.flex {
        border-top: 1px solid gray;
    }
    .black {
        color: white;
    }
}
}
