css:
@-moz-document domain(fivl.it) {

.span6 {
    margin-bottom: 2em;
}
.jsn-articlecols {
    background: #f9f9f9;
    border-radius: 6px;
    overflow: hidden;
    position: relative;
    width: 100%;
}
a.readon {
    box-sizing: border-box;
    height: 100%;
    width: 100%;
    border: solid 1px #f9f9f9;
    position: absolute;
    right: 0;
    top: 0;
    padding: 0;
    color: transparent;
}

a.readon:hover {
    background: none;
    background: rgba(0, 154, 202, .1);
    border: dashed 1px rgb(0, 154, 202);
    color: transparent;
}
.contentheading {
    font-weight: bold;
    font-size: large;
}
.jsn-article-toolbar {
    background: none;
    margin: 0;
}

.jsn-article-info,
ul.jsn-article-tools {
    padding: 0;
    margin: 0;
}

a:hover {
    text-decoration: none;
    color: #005580;
}

#jsn-gotoplink {
    display: none!important;
}
#jsn-pos-promo-right {
    padding: 0;
    overflow: hidden;
}
#jsn-pos-promo-right .banneritem img {
    height: 296px;
}

#jsn-content-top, #jsn-pos-promo-right{
    background:#404040;
    height:300px;
    border:none;
}

#jsn-menu{
    font-weight:bold;
}

}
