css:
@-moz-document domain("smartsheet.com") {
html {
	-webkit-filter: invert(100%);
    background: #fff;
}
body {
	background: #fff;
}
img,
iframe {
	-webkit-filter: invert(100%);
}
a, a:visited, .model-link, a:link {
 	text-decoration: none;
    color: #912BA1;
}
a:hover, .model-link:hover {
 	border-bottom: 1px solid #ccc;   
}
.inverse {
 	color: #000;   
}
}
