css:
@-moz-document domain("intranet") {

.x-window-body > .x-border-layout-ct > .syno-d-main-toolbar {
    width: 39px !important;
}
.x-window-body > .x-border-layout-ct > .syno-d-main-toolbar > * {
    left: auto !important;
}





.ps__thumb-x,
.ps__thumb-y {
    border-radius: 0;
    /*background: #56585A;*/
    background: #C4C6C8;
}
.ps__thumb-x {
    height: 14px;
    bottom: auto;
}
.ps__thumb-y {
    width: 14px;
    right: auto;
}


body.syno-o-sheet-theme .ps__rail-x:hover > .ps__thumb-x,
body.syno-o-sheet-theme .ps__rail-x:focus > .ps__thumb-x,
body.syno-o-sheet-theme .ps__rail-y:hover > .ps__thumb-y,
body.syno-o-sheet-theme .ps__rail-y:focus > .ps__thumb-y {
    background: #2DB27B;
}
body.syno-o-sheet-theme .ps__thumb-x.syno-o-bar-dragging,
body.syno-o-sheet-theme .ps__thumb-y.syno-o-bar-dragging {
    background: #1e9362 !important;
}



body.syno-o-theme .ps__rail-x:hover > .ps__thumb-x,
body.syno-o-theme .ps__rail-x:focus > .ps__thumb-x,
body.syno-o-theme .ps__rail-y:hover > .ps__thumb-y,
body.syno-o-theme .ps__rail-y:focus > .ps__thumb-y {
    background: #4987F2;
}
body.syno-o-theme .ps__thumb-x.syno-o-bar-dragging,
body.syno-o-theme .ps__thumb-y.syno-o-bar-dragging {
    background: #316cd2 !important;
}
}
