css:
@-moz-document url-prefix("https://www.deviantart.com/submit/") {
/* Checkbox, Mature Content -- cmcsize, default: 20%*/
  #ile-contents > div.ile-editor-bottom > div.ile-step-one.ile-step-bottom > div.ile-eclipse-options > div.ile_mod.ile-mature > label {
      width: /*[[cmcsize]]*/% !important;
  }

/* Checkbox, Scraps -- cscsize, default: 20%*/
  #ile-contents > div.ile-editor-bottom > div.ile-step-one.ile-step-bottom > div.ile-eclipse-options > div.ile_mod.ile-scraps > label {
      width: /*[[cscsize]]*/% !important;
  }

/* Checkbox, Allow Comments -- cacsize, default: 15%*/
  #ile-contents > div.ile-editor-bottom > div.ile-step-one.ile-step-bottom > div.ile-section.ile-section-options > div.ile-section-options-publish.ile-section-options-container > div > div.ile_mod.ile-comments > label {
      width: /*[[cacsize]]*/% !important;
  }

/* Checkbox, Submission Policy -- cspsize, default: 35%*/
  #ile-contents > div.ile-editor-bottom > div.ile-section.ile-section-agreements.ile-publish-section.ile-publish-section-agreements > div:nth-child(1) > label {
      width: /*[[cspsize]]*/% !important;
  }

/* Checkbox, License Agreement -- clasize, default: 35%*/
  #ile-contents > div.ile-editor-bottom > div.ile-section.ile-section-agreements.ile-publish-section.ile-publish-section-agreements > div.tos-agreement.agreement.required > label {
      width: /*[[clasize]]*/% !important;
  }

/* Checkbox, Remember Size -- crssize, default: 25%*/
  #ile-contents > div.ile-editor-bottom > div.ile-section.ile-section-remember > div > label {
      width: /*[[crssize]]*/% !important;
  }
}
