css:
@-moz-document domain("anonimowe.pl") {
:root{
    scrollbar-color: #444444 #333333 !important
}
.container .add-post, .container article.story, .container article.story header.story-header time,.container.story-form .story-rules{
    background: #444;
}
.container article.story section,.container.story-form .story-rules h3,.container.story-form .story-rules ul,.container.story-form article .counter{
    color: #fff;
}
.container article.story .comments-container .comments-list .comment{
    background: #444;
}
.container article.story .comments-container .comments-list .comment .author{
    color: yellowgreen;
}
.container article.story .comment footer .vote-container span{
    color: #fff;
}
.container .sidebar .box{
    background: #444;
}
.container .sidebar .box h3{
    color: #fff;
}
.container article{
    background: #444;
}
.container .user-settings article .setting-section header{
    color: #fff;
}
.row:after{
    content: "Dark theme by Maciejozaman";
    color: #d9d9d9;
    margin-left: 15px;
}
}
