.logo_text {
    display: none!important;
}
#site_description {
    display: none!important;
}
#left_col {
    margin: 0 0 0 0!important;
}
div.post_wrap:nth-child(1) > div:nth-child(2) {
    display: none!important;
}
.header_menu ul {
    float: left;
}
.header_menu {
    width: 79.3%;
}
#header {
    background: left top;
    height: 35px!important;
    border-bottom: 0px solid #ccc;
    box-shadow: 0px 0px 0px 0px #aaa;
}
.meta ul {
    display: none!important;
}
#right_col {
    display: none!important;
}
#footer {
    display: none!important;
}
.header_menu {
    border-bottom: 0px solid #ccc;
