css:
@-moz-document url-prefix("https://voat.co/") {
body {
    background: #34163d;
}
#container {
    background: #532e5e;
}
.submission:nth-child(2n) {
    background: #7a2793;
}
.side .spacer.spacersection {
    background: #6b5472;
}
#header-container {
    background: #632277;
}
.top-bar {
    background: #594f5b;
}
.header {
    background: #100514;
}
.link .usertext .md {
    background: #4b2d54;
}
.comment.even, .panel-body {
    background: #392f3d;
}
.comment.odd, .panel-heading {
    background: #320f3d;
}
.commenttextarea {
    background: #1a0e1e;
}
.form-control {
    background: #1b171c;
}
.friendly .md .panel-message-body, #messageContainer .panel-message-body, .entry {
    background: #3d0d4c;
}
.friendly .md .panel-heading-messagetitle, #messageContainer .panel-heading-messagetitle {
    background: #524256;
}
.usertext .md blockquote {
    background: #2e073a;
}
}
