css:
/* i really want this to be global */
@-moz-document domain("amazon.co.jp") {


/* オーバーレイをなくす */
.gradientOverlay,
.pausedOverlay {
  background: transparent !important;
}

/* 再生・移動ボタンを丈夫に移動する */
.buttons {
  position: absolute;
  top: 0;
  left: 0;
}

/* 次のタイトル表示をなくす */
.nextUpWrapper,
.nextUpContents {
  display: none;
}

/* シークバーのサムネイルを小さくする */
.trickplayImage {
  min-width: 100px !important;
  width: 100px !important;
}

/* シークバーの再生時間を小さくする */
.trickplayTime {
  font-size: .75vw !important;
}


}
