css:
/* i really want this to be global */
@-moz-document domain("netflix.com") {


/* 
 * エンディングで画面を小さくしない
 * https://textuploader.com/dgrt6
 */
.AkiraPlayer .nfp.nf-player-container.NFPlayer.postplay {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  min-height: 160px;
  border: 0 solid transparent;
  cursor: default;
}
.NFPlayer.postplay {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: 0 solid #eee;
  z-index: 2;
}
.AkiraPlayer .nfp.nf-player-container.NFPlayer.postplay:hover {
  border: 0 solid #fff;
}

/* シークバーのサムネイルを小さくする */
.trickplay-text-and-image {
  width: 65px !important;
}

/* シークバーの再生時間を小さくする */
.tp-text {
  font-size: 1em !important;
}


}
