css:
@-moz-document domain(music.yandex.ru) {
/* Global */

:root ::-webkit-scrollbar {
    -webkit-appearance: none;
    width: 2px;
    height: 2px;
}

:root ::-webkit-scrollbar-thumb {
    cursor: pointer;
    background: #ffdb4d;
}
:root ::-webkit-scrollbar-track {
    background: #181818;
}

.page-root, .bar, .loading, .theme_dark {
    max-width: 100%;
}

.centerblock-wrapper {
    width: 1425px;
}

.sidebar {
    height: 100% !important;
}

/* Radio */

.page-radio__dashboard-column {
    width: 25%;
    margin-left: 0;
    margin-right: 0;
}

.radio-station {
    margin: 0 auto;
}
}
