css:
@-moz-document url-prefix("https://avgle.com/video") {
body {
    padding-top: 20px;
}
.top-nav {
    display: none;
}
.navbar-fixed-top {
    top: 0;
    margin-bottom: 0;
    background-color: unset;
}
ul.navbar-right > li.hidden-sm, ul.navbar-right > li.hidden-xs {
    display: none;
}
ul.navbar-right > li {
    padding-right: 0 !important;
}
#wrapper {
    padding-bottom: 0 !important;
}
#wrapper > div {
    display: none;
}
#wrapper > .container {
    display: block;
}

/* Show only title, video and suggested video in class container. */
#wrapper > .container > div,
#wrapper > .container > ul {
    display: none;
}
#wrapper > .container > .row,
#wrapper > .container > .tab-content {
    display: block;
}

/* Modify the title, make it fit into the header */
#wrapper > .container > .row:nth-child(1) {
    height: 40px;
    margin: 0 280px 0 100px;
}
#wrapper > .container > .row:nth-child(1) > div {
    z-index: 1031;
}
#wrapper > .container > .row:nth-child(1) .btn {
    display: none;
}

/* Removes ads inside video row. */
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) {
    width: 100%;
}
#wrapper > .container > .row:nth-child(2) > div:nth-child(2) {
    display: none;
}

/* Removes useless supporting components around video . */
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .fps60-text-icon,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .hd-text-icon,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .vote-box,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .separator,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .pull-left,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .pull-right,
#wrapper > .container > .row:nth-child(2) > div:nth-child(1) > .m-t-10 {
    display: none;
}

#flash {
    padding-top: 0 !important;
}
#adxp {
    display: none;
}
.alert {
    display: none;
}
iframe {
    display: none;
}
#g-recaptcha iframe {
    display: block;
}

#wrapper > div.container > div.hidden-xs {
    display: block;
}
#aoverlay {
    display: none;
}
#player_3x2_container {
    position: absolute;
    width: 100%;
    height: 100%;
    z-index: 1;
}
#player_3x2_container_inner {
    position: unset !important;
    min-height: unset !important;
    transform: unset !important;
    width: 100%;
    height: 100%;
}
#player_3x2_close {
    margin-top: 0 !important;
    height: 100%;
    top: 0 !important;
    padding: 0 !important;
    background-color: rgba(0, 0, 0, 0.3);
}
#vjs-logobrand-image-destination {
    display: none;
}
}
