css:
@-moz-document domain("tl.krakow.pl") {
body {
    background: #080808;
}
#page {
	background-color: #141414;
}
h2 {
	color: #ddd;
}
div.news span.title a {
	color: #e0e0e0 !important;
}
div#content {
	color: #ccc;
}
div.news span.author {
	color: #8c8c8c;
}
div#content li {
	color: #bbb;
}
div#content h4 {
	color: #eee;
}

div#sidebar a {
	color: #ddd;
}
ul#menu a {
	color: #ddd;
}
ul#menu li:hover > ul {
	background: #141414;
}
ul#menu ul.hover {
	background: #000;
}
#sections a {
    background: none;   
}
#sections a:hover {
    background: none;
    color: #fff !important;
}

.ns-widget {
    background: #111;
}
.ns-widget .ns-logo {
  border-right-color: #444;
}
.ns-widget.greyFrame .ns-news,
.ns-widget.greyFrame .ns-numbers,
.ns-widget.greyFrame .ns-search,
.ns-widget.greyFrame .ns-gap {
  border-left-color: #333;
  border-right-color: #333;
}
.ns-widget .ns-numbers li {
  color: #ddd !important;
}
.ns-widget .ns-header {
  color: #ddd !important;
}
}
