.pausedOverlay .buttons div{
cursor: pointer!important;
}
.cascadesContainer > div > .webPlayer .gradientOverlay{
background: transparent!important;
}

.topPanel .center{
position: absolute;
bottom: 10px;
left: 4%;
}
.contentTitlePanel{
display: flex;
}
.contentTitlePanel .title{
   font-size: 22px!important;
   margin-right: 10px!important;
    opacity:0.5;
}
.contentTitlePanel .subtitle{
font-size: 16px!important;
      opacity:0.5;
}
.hdIcon {
    opacity:0.5;
    width:25px !important;
    height:auto;
}
.time {
    font-size:16px !important;
}
.collapsibleXrayHeader {
    visibility:hidden !important;
}
.hd1080p {
    font-size:12px !important;
    opacity:0.5;
}
.topButtons {
    opacity:0.4;
}
.nextTitleButton {
    font-size:14px !important;
    opacity:0.5;
}
.nextTitleButton .icon  {
visibility:hidden !important;
}
}

@-moz-document domain("primevideo.com") {
.buttons, .f1fo23vz {
    position: absolute;
bottom: -10px;
left: 35%;
transform: scale(0.6);
z-index:9;
}
.cascadesContainer > div > .webPlayer .pausedOverlay, .fddsvlq, .f8hspre, .f1icw8u{
cursor: default!important;
}
.xrayQuickViewList {
    visibility: hidden !important;
}
}
