css:
@-moz-document domain("twitter.com") {
header > div > div {
    left: 10px;
}

/* move sidebar to right */
main > div > div:nth-child(2) > div > div:nth-child(2) {
    position: fixed;
    right: 10px;
    /*display: none;*/
}

/* Hide all but search from side */
/*main > div > div:nth-child(2) > div > div:nth-child(2) > div > div:nth-child(2) > div > div > div > div > div {*/
div[data-testid="sidebarColumn"] > div > div:nth-child(2) > div > div > div > div {   
    display: none;
}

/*
main > div > div:nth-child(2) > div > div:nth-child(2) > div > div:nth-child(2) > div > div > div > div > div:first-child,*/
div[data-testid="sidebarColumn"] > div > div:nth-child(2) > div > div > div >div:first-child{
    display: initial;
    margin-top: 10px;
}

main > div > div:nth-child(2) > div > div:first-child {
    margin-left: 0;
}

header > div > div > div > div > div:nth-child(2) > nav > a > div > div:nth-child(2),
header > div > div > div > div > div:nth-child(2) > nav > div > div > div:nth-child(2){
    display: none;
}

header > div > div > div > div > div:nth-child(3) > a{
    width: 50px;
    max-width: 50px;
    min-width: 50px;
    padding: 0;
}
}
