css:
@-moz-document url-prefix("https://news.ycombinator.com") {
    html, table {
        background: #222 !important;
    }
    .pagetop {
        color: #fff !important;
    }
    a {
        color: #fff !important;
    }
    .storylink {
        color: #ff6f36 !important;
    }
    a:visited {
        color: #999 !important;
    }
    .spacer {
        height: 10px !important;
    }
    form input {
        color: #999 !important;
    }
    #pagespace {
        background: #222 !important;
        height: 25px !important;
    }
    #hnmain {
        background: #222 !important;
    }
    .subtext {
        padding-top: 5px !important;
    }
}
