css:
@-moz-document url-prefix("http://www.livebasketball.tv/live/event/"), url-prefix("http://www.livebasketball.tv/live/clip/") {
.Header, .Footer, .closeBtnSpielerWrapper, #videoEventListWrapper {
    display: none !important;
}
.pageLive, .mainContainer {
    margin: 0;
    padding: 0 !important;
}
.mainContainer {
    max-width: 100vw;
}
.playerWrapper {
    max-height: calc(100vw * 0.5625) !important;
    height: calc(100vw * 0.5625) !important;
}
}
