css:
@-moz-document url-prefix("https://lykil.fo/") {
body {
	background-color: #333;
}

.navbar.navbar-default.navbar-static-top {
	background-image: none !important;
	background-color: #555;
}

.navbar-brand img {
	filter: invert(100%);
}

.panel {
	background-color: #555;
}

.panel-primary > .panel-heading {
	background-color: #777 !important;
	background-image: none !important;
}

.panel-footer {
	background-color: #777 !important;
}

.panel-footer a,
.panel-body #mainForm .form-group .control-label,
.panel-body h4, .panel-body p,
.panel-footer.text-center{
	color: white;
}

.panel-body #mainForm .form-group input {
	background-color: black;
	color: white;
}
}
