css:
@-moz-document domain("swift.org") {
body,
html {
    background-color: #333;
    background: #333;
    color: #ddd;
    scrollbar-color: #666 #333;
}

p,
h1,
h2,
h3,
h4,
h5,
h6 {
    color: azure;
}

p > code.literal,
li > code.literal,
dt > code.literal,
dd > code.literal {
    color: #999;
}

nav[role="navigation"] {
    background-color: #222;
    background: #222;
    color: azure !important;
}

nav {
    background-color: #222;
    background: #222;
}

nav a,
nav h2 {
    color: azure !important;
}

nav[role="navigation"] ul li ul a.current:link {
    text-shadow: 3px 3px 5px black;
}

.admonition-beta > p:nth-child(2) {
    color: #444;
}

.highlight {
    background: #aaa;
    text-shadow: 0px 2px 7px rgba(0, 0, 0, .4);
}

.last {
    color: #bbb;
}

.highlight .gi {
    background-color: #ddd;
}

.admonition {
    background-color: #222;
}

img {
    filter: invert(80%);
}

.info{    
background-color: #444;
}
}
