css:
@-moz-document domain("login.emu.dk") {
body{
	background-color: #333;
}

#header,
#outside,
#footer,
#pwd input{
	background-color: black;
}

#header h1,
label,
#ellerLog,
.floatright,
#pwd input,
#pwd input::placeholder,
div{
	color: white;
}

#pwd input{
	border: 1px solid white;
	padding: 5px;
	margin-top: 2px;
}

#nemidIcon,
#maxilogo{
	filter: invert(100);
}
}
