css:
@-moz-document domain("dispostable.com") {
body {
    background: #000;
    color: #fff;
}
* {
    -webkit-filter: invert(100%);
    filter: invert(100%);
}
p {
    color: #000;
}
span {
    color: #fff;
}
#content > p:nth-child(3) {
    color: #fff;   
}
html body div#content form#inbox {
    border-radius: 8px; 
    background-color: #000;
}
#recent, #inform {
    border-radius: 8px;
    border: 1px solid #B1AFAD;
    padding-left: 16px;
}
#content > p:nth-child(6) {
    color: #fff;
}
iframe,#crumbs {
    border-radius: 8px;
}
}
